mapper.select:
SELECT

FROM OFFICE_COMMUTE_DAY a

WHERE a.BASE_DATE_STR = #{baseDateStr} AND a.user_id = #{userId}

mapper.update:
UPDATE OFFICE_COMMUTE_DAY
SET
	start_work_date = NOW()
	,start_work_ip = #{startWorkIp}
	,work_status_code = #{workStatusCode}
	,work_result_code = #{workResultCode}
	,mod_date = NOW()
	,mod_user_id = #{loginUserId}
WHERE base_date_str = #{baseDateStr} and user_id = #{userId}

UPDATE OFFICE_COMMUTE_DAY
SET
	out_work_date = NOW()
	,out_work_ip = #{outWorkIp}
	,work_status_code = #{workStatusCode}
	,work_result_code = #{workResultCode}
	,worked_time_value = #{workedTimeValue}
	,mod_date = NOW()
	,mod_user_id = #{loginUserId}
WHERE base_date_str = #{baseDateStr} and user_id = #{userId}

mapper.select:
SELECT

FROM OFFICE_COMMUTE_DAY a

WHERE a.BASE_DATE_STR = #{baseDateStr} AND d.dept_key = #{deptKey}

mapper.update:
UPDATE OFFICE_COMMUTE_DAY
SET
	mod_date = NOW()

		,start_work_date = #{startWorkDate}


		,out_work_date = #{outWorkDate}


		,final_start_work_date = #{finalStartWorkDate}


		,final_out_work_date = #{finalOutWorkDate}


		,start_work_ip = #{startWorkIp}


		,out_work_ip = #{outWorkIp}


		,outside_work_yn = #{outsideWorkYn}


		,work_status_code = #{workStatusCode}


		,work_result_code = #{workResultCode}


		,vacation_kind_code = #{vacationKindCode}


		,etc_description = #{etcDescription}


		,mod_yn = #{modYn}


		,tardy_yn = #{tardyYn}


		,worked_time_value = #{workedTimeValue}


		,mod_user_id = #{modUserId}

WHERE base_date_str = #{baseDateStr} AND user_id = #{userId}

mapper.select:
SELECT

FROM OFFICE_COMMUTE_DAY a

WHERE a.BASE_DATE_STR = #{baseDateStr}
	AND d.dept_key IN

		#{item}

SELECT 'tardy'   AS kind /* 지각 */
	,count(*) AS agg_count
FROM   office_commute_day
WHERE  user_id = #{userId}
	AND substring(base_date_str :: VARCHAR, 1, 6) = #{searchMonthStr}
	AND tardy_yn = 'Y'
UNION ALL
SELECT 'not_start_work' AS kind /* 출근 미체크 */
	,count(*)        AS agg_count
FROM   office_commute_day
WHERE  user_id = #{userId}
	AND substring(base_date_str :: VARCHAR, 1, 6) = #{searchMonthStr}
	AND start_work_date IS NULL
UNION ALL
SELECT 'not_out_work' AS kind /* 퇴근 미체크 */
	,count(*)      AS agg_count
FROM   office_commute_day
WHERE  user_id = #{userId}
	AND substring(base_date_str :: VARCHAR, 1, 6) = #{searchMonthStr}
	AND out_work_date IS NULL

SELECT

FROM OFFICE_COMMUTE_DAY a

WHERE 1=1

	AND a.base_date_str = #{baseDateStr}


	AND a.user_id = #{userId}


	AND d.dept_key = #{deptKey}


	AND a.tardy_yn = 'Y'


	AND (
		a.work_status_code LIKE 'VACATION%'
			AND a.work_status_code NOT IN( 'VACATION_MORNING', 'VACATION_AFTERNOON' )
	)


	AND substring(a.base_date_str :: VARCHAR, 1, 6) = #{searchMonthStr}


	AND a.base_date_str  >=  #{startDateStr}


	AND a.base_date_str  <=  #{endDateStr}


	AND d.dept_key IN

		#{item}

mapper.update:
UPDATE OFFICE_COMMUTE_DAY
SET
	dept_submit_yn = 'Y'
	,mod_date = NOW()
	,mod_user_id = #{loginUserId}
WHERE base_date_str = #{baseDateStr} and user_id = #{userId}

mapper.select:
SELECT u.user_key AS user_key /* 사용자키 */ ,u.user_name AS user_name /* 사용자명 */ ,u.dept_key AS dept_key /* 부서키 */ ,u.rank_key AS rank_key /* 직급명 */ ,u.duty_key AS duty_key /* 직책키 */ ,u.mobile_tel /* 핸드폰번호 */ ,u.email /* 이메일 */ ,u.position_key AS position_key /* 직위키 */ ,d.dept_name AS dept_name /* 부서명 */ ,r.rank_title AS rank_title /* 직급명 */ ,du.duty_title AS duty_title /* 직책명 */ ,po.position_title AS position_title /* 직위명 */ ,(SELECT count(*) FROM office_commute_day WHERE user_id = u.user_key
AND substring(base_date_str :: VARCHAR, 1, 6) = #{searchMonthStr}
AND base_date_str >= #{startDateStr}
AND base_date_str <= #{endDateStr}
AND start_work_date IS NOT NULL AND tardy_yn = 'N') AS success_commute_count /* 정상출근 수 */ ,(SELECT count(*) FROM office_commute_day WHERE user_id = u.user_key
AND substring(base_date_str :: VARCHAR, 1, 6) = #{searchMonthStr}
AND base_date_str >= #{startDateStr}
AND base_date_str <= #{endDateStr}
AND tardy_yn = 'Y') AS tardy_commute_count /* 지각 수 */ ,(SELECT count(*) FROM office_commute_day WHERE user_id = u.user_key
AND substring(base_date_str :: VARCHAR, 1, 6) = #{searchMonthStr}
AND base_date_str >= #{startDateStr}
AND base_date_str <= #{endDateStr}
AND ( work_status_code LIKE 'VACATION%' AND work_status_code NOT IN( 'VACATION_MORNING', 'VACATION_AFTERNOON' ) )) AS vacation_count /* 휴가/휴직 수 */ FROM to0_user_main u LEFT OUTER JOIN to0_dept_mast d ON u.dept_key = d.dept_key LEFT OUTER JOIN to0_rank_main r ON u.rank_key = r.rank_key LEFT OUTER JOIN to0_duty_main du ON u.duty_key = du.duty_key LEFT OUTER JOIN to0_position_main po ON u.position_key = po.position_key WHERE u.dept_key = #{deptKey}
